<?phpadd_action( 'wp_ajax_load_more_search', 'load_more_search' );add_action( 'wp_ajax_nopriv_load_more_search', 'load_more_search' );function load_more_search() {	if ( ! isset( $_POST['nonce'] ) && ! wp_verify_nonce( $_POST['nonce'] ) ) {		die( 'access deind' );	}	$paged = isset($_POST['paged']) ? absint($_POST['paged']) : 1;	$search_value = isset($_POST['search_value']) ? sanitize_text_field($_POST['search_value']) : '';	$args = [		'post_type' => 'post',		'posts_per_page' => 2,		'paged' => $paged,		's' => $search_value,		'post_status' => 'publish'	];	$the_query = new WP_Query($args);	$html_output = '';	if ($the_query->have_posts()):		while ($the_query->have_posts()) : $the_query->the_post();			$cat_id = get_post_meta(get_the_ID(), '_tnw_category_post', true);			$cat_name = $cat_id ? get_the_category_by_ID($cat_id) : 'بدون دسته';			$cat_link = $cat_id ? get_category_link($cat_id) : '#';			$id_or_email = get_the_author_meta('email');			$avatar = get_avatar($id_or_email, '39', 'robohash', get_the_author(), ['class' => 'avatar-img rounded-circle']);			$html_output .= '<div class="card border rounded-3 up-hover p-4 mb-4">                <div class="row g-3">                    <div class="col-sm-9">                        <a href="' . esc_url($cat_link) . '" class="badge text-bg-' . ($cat_id ? 'success' : 'danger') . ' mb-2">                            <i class="fas fa-circle me-2 small fw-bold"></i>' . esc_html($cat_name) . '                        </a>                        <h4 class="card-title">                            <a href="' . esc_url(get_permalink()) . '" class="btn-link text-reset stretched-link">' . esc_html(get_the_title()) . '</a>                        </h4>                        <ul class="nav nav-divider align-items-center d-none d-sm-inline-block">                            <li class="nav-item">                                <div class="nav-link">                                    <div class="d-flex align-items-center position-relative">                                        <div class="avatar avatar-xs">' . $avatar . '</div>                                        <span class="ms-3">با <a href="' . esc_url(get_author_posts_url(get_the_author_meta('ID'))) . '" class="stretched-link text-reset btn-link">' . esc_html(get_the_author()) . '</a></span>                                    </div>                                </div>                            </li>                            <li class="nav-item">' . esc_html(get_the_date('j ,F Y')) . '</li>                            <li class="nav-item"><span class="fa fa-comment"></span> ' . esc_html(get_comments_number()) . '</li>                        </ul>                    </div>                    <div class="col-sm-3">' . tnw_post_thumbnail() . '</div>                </div>            </div>';		endwhile;	else:		$html_output = '<div class="alert alert-info">مطالب بیشتری یافت نشد</div>';	endif;	wp_reset_postdata();	wp_send_json([		'success' => true,		'content' => $html_output,		'max_num' => $the_query->max_num_pages,		'current_page' => $paged	]);}