<?phpadd_action('wp_ajax_nopriv_tnw_verification_code_verify','tnw_verification_code_verify');function tnw_verification_code_verify() {	if (!isset($_POST['nonce']) && !wp_verify_nonce($_POST['nonce'])){		die('access deind');	}	$verification_code = sanitize_text_field($_POST['verification_code']);	tnw_verification_code_validate($verification_code);	tnw_check_verification_code($verification_code);}// Validatefunction tnw_verification_code_validate($verification_code) {	if (empty($verification_code)){		wp_send_json(['error'=>true,'message'=>'کد تایید خود را وراد کنید'],403);	}	if(strlen($verification_code) != 6 ){		wp_send_json(['error' => true, 'message' => 'کد تایید باید 6 رقم باشد!'], 403);	}	if (!is_numeric($verification_code)){		wp_send_json(['error' => true, 'message' => 'کد امنیتی حتما باید عدد باشد!!'], 403);	}}// CHECK VERIFICATION CODEfunction tnw_check_verification_code($verification_code) {	global $wpdb;	$table = $wpdb->prefix.'tnw_verify_code';	$stmt = $wpdb->get_row($wpdb->prepare("SELECT verification_code FROM {$table} WHERE verification_code = %s", $verification_code));	if ($stmt) {		wp_send_json(['success' => true, 'message' => 'کد تاییدیه معتبر است!'], 200);		wp_tnw_verification_code_to_db_delete($verification_code);	} else {		wp_send_json(['error' => true, 'message' => 'کد تاییدیه معتبر نمی‌باشد!'], 403);	}}