<?phpfunction tnw_comment_func($comment , $args) {	$comment = $GLOBALS['comment'];	?>  <li <?php comment_class('mb-4 d-flex') ?> id="comment-<?php comment_ID(); ?>">	  <?php echo get_avatar($comment->email,'','robohash',$comment->comment_author,[ 'class' => " avatar avatar-md rounded-circle float-start me-3" ]); ?>	  <div>		  <div class="mb-2">			  <h5 class="m-0"><?php echo $comment->comment_author; ?></h5>			  <span class="me-3 small"><?php echo get_comment_date( 'j F, Y' ); ?></span>              <!-- دکمه پاسخ -->			  <?php if ( is_user_logged_in() ) : ?>                  <a href="#" class="text-body fw-normal reply"                     data-bs-toggle="modal"                     data-bs-target="#comment-modal"                     data-comment-id="<?php echo $comment->comment_ID; ?>"                     data-comment-author="<?php echo $comment->comment_author; ?>"                     data-main-comment-content="<?php echo esc_attr($comment->comment_content); ?>">                      <span class="icona"><i class="ti-back-right"></i></span> پاسخ                  </a>			  <?php endif; ?>              <!-- لینک ویرایش برای ادمین -->			  <?php			  if ( current_user_can( 'manage_options' ) ) {				  edit_comment_link( '<i class="fal fa fa-edit text-danger"></i>' );			  }			  ?>          </div>          <!-- محتوای کامنت -->		  <?php if ( ! $comment->comment_approved ) : ?>              <div class="alert alert-info">منتظر تایید ثبت دیدگاه خود باشد!</div>		  <?php else: ?>              <p><?php echo $comment->comment_content; ?></p>		  <?php endif; ?>      </div>  </li>    <!-- نمایش فرزندان کامنت به صورت تو در تو -->	<?php	if ( 'div' !== $args['style'] ) {		echo '</li>';	} }