<?phpfunction activation_file() {	global $wpdb;	$table_verify_code = $wpdb->prefix . 'tnw_verify_code';	$tnw_verify_code = "CREATE TABLE IF NOT EXISTS `$table_verify_code` (        `id` int(11) NOT NULL AUTO_INCREMENT,        `verification_code` varchar(6) NOT NULL,        `phone` varchar(15) NOT NULL,        `status` int(11) NOT NULL DEFAULT 0 COMMENT '0: unverify, 1: verify',        `create_date` datetime NOT NULL DEFAULT current_timestamp(),        `update_at` datetime NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),        PRIMARY KEY (`id`)    ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci";	$table_newsletter = $wpdb->prefix . 'tnw_newsletter';	$tnw_newsletter = "CREATE TABLE IF NOT EXISTS `$table_newsletter` (        `id` int(11) NOT NULL AUTO_INCREMENT,        `email` varchar(256) NOT NULL,        `status` int(1) NOT NULL DEFAULT 1 COMMENT 'active: 1, deactive: 0',        `create_at` datetime NOT NULL DEFAULT current_timestamp(),        `update_at` datetime NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),        PRIMARY KEY (`id`),        UNIQUE KEY `email` (`email`)    ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci";	$table_token = $wpdb->prefix . 'tnw_validate_token';	$tnw_validate_token = "CREATE TABLE IF NOT EXISTS `$table_token` (        `id` int(11) NOT NULL AUTO_INCREMENT,        `token` varchar(256) NOT NULL,        `email` varchar(256) NOT NULL,        `status` int(11) NOT NULL,        `create_at` datetime NOT NULL DEFAULT current_timestamp(),        `update_at` datetime NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),        PRIMARY KEY (`id`),        INDEX (`token`),        INDEX (`email`)    ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci";	require_once(ABSPATH . 'wp-admin/includes/upgrade.php');	dbDelta($tnw_verify_code);	dbDelta($tnw_newsletter);	dbDelta($tnw_validate_token);}function deactivation() {}add_action('after_switch_theme', 'activation_file');register_deactivation_hook('switch_theme', 'deactivation');