<?phpfunction wp_tnw_verification_code_to_db($verification_code, $user_phone_number) {	global $wpdb;	$table = $wpdb->prefix . 'tnw_verify_code';	$stmt = $wpdb->get_row($wpdb->prepare("SELECT phone FROM {$table} WHERE phone = %s", $user_phone_number));	if ($stmt) {		$data = ['verification_code' => $verification_code];		$where = ['phone' => $user_phone_number];		$format = ['%s'];		$where_format = ['%s'];		$wpdb->update($table, $data, $where, $format, $where_format);	} else{		$data = [			'phone' => $user_phone_number,			'verification_code' => $verification_code		];		$format = ['%s', '%s'];		$wpdb->insert($table, $data, $format);	}}function wp_tnw_verification_code_to_db_delete($verification_code) {	global $wpdb;	$table = $wpdb->prefix . 'tnw_verify_code';	$where = ['verification_code' => $verification_code];	$where_format = ['%s'];	return $wpdb->delete($table, $where, $where_format);}