<?php// Looking to send emails in production? Check out our Email API/SMTP product!function tnw_send_mail_config($phpmailer) {	$setting_website     = get_option( '_tnw_settings_set');	$phpmailer->isSMTP();	$phpmailer->Host     = $setting_website['smtp_data']['host'];	$phpmailer->SMTPAuth = true;	$phpmailer->Port     =$setting_website['smtp_data']['port'];	$phpmailer->Username = $setting_website['smtp_data']['username'];	$phpmailer->Password = $setting_website['smtp_data']['password'];	// Sender and recipient settings	$phpmailer->From = $setting_website['smtp_data']['from'];;	$phpmailer->FormName = $setting_website['smtp_data']['FormName'];}add_action('phpmailer_init', 'tnw_send_mail_config');