<?phpfunction tnw_post_thumbnail_default(): string {	$thumb_logo = TNW_URL . '/assets/images/img/technoavar.jpg';	return "<img  class='img-thumbnail'  src='" . esc_url($thumb_logo) . "' alt='image'>";}function tnw_post_thumbnail(): string {	if (has_post_thumbnail()){	  return get_the_post_thumbnail('','full',			[				'class'=>'img-responsive card-img',				'alt'=>get_the_title()		]);	} else{	 return	tnw_post_thumbnail_default() ;	}}function tnw_post_thumbnail_url(): string {	if (has_post_thumbnail()){		return '<div class="card bg-dark-overlay-5 overflow-hidden card-bg-scale h-400 text-center" style="background-image:url(' .get_the_post_thumbnail_url().'); background-position: center left; background-size: cover;">';	}else{		return '<div class="card bg-dark-overlay-5 overflow-hidden card-bg-scale h-400 text-center" style="background-image:url('.TNW_URL.'/assets/images/img/technoavar.jpg'.'); background-position: center left; background-size: cover;">';	}}