<?phpadd_action( 'admin_menu', 'tnw_register_menu' );function tnw_register_menu() {	add_menu_page(		'تنظیمات پیشرفته قالب',		'تنظیمات پیشرفته قالب',		'manage_options',		'setting_themes_technoavar',		'setting_themes_html',		'data:image/svg+xml;base64,'		,60    );	add_submenu_page(		'setting_themes_technoavar',		'تنظیمات عمومی',		'تنظیمات عمومی',		'manage_options',		'setting_themes_technoavar_tab_one',		'setting_themes_html',		99	);	add_submenu_page(		'setting_themes_technoavar',		'مدیریت اسلایدر',		'مدیریت اسلایدر',		'manage_options',		'setting_themes_technoavar_tab_two',		'setting_themes_html',		99	);	add_submenu_page(		'setting_themes_technoavar',		'منتخب سردبیر',		'منتخب سردبیر',		'manage_options',		'setting_themes_technoavar_tab_three',		'setting_themes_html',		99	);	add_submenu_page(		'setting_themes_technoavar',		'شبکه‌های اجتماعی',		'شبکه‌های اجتماعی',		'manage_options',		'setting_themes_technoavar_tab_four',		'setting_themes_html',		99	);	add_submenu_page(		'setting_themes_technoavar',		'مدیریت تبلیغات',		'مدیریت تبلیغات',		'manage_options',		'setting_themes_technoavar_tab_five',		'setting_themes_html',		99	);}function setting_themes_html() {	if ( ! current_user_can( 'manage_options' ) ) {		wp_die( 'دسترسی غیرمجاز!' );	}	$page = isset($_GET['page']) ? $_GET['page'] : 'setting_themes_technoavar_tab_one';	$tab  = str_replace('setting_themes_technoavar_', '', $page);	?>    <div class="wrap">        <h1 class="nav-tab-wrapper">            <a href="?page=setting_themes_technoavar_tab_one" class="nav-tab <?php echo $tab === 'tab_one' ? 'nav-tab-active' : ''; ?>">تنظیمات عمومی</a>            <a href="?page=setting_themes_technoavar_tab_two" class="nav-tab <?php echo $tab === 'tab_one' ? 'nav-tab-active' : ''; ?>">مدیریت اسلایدر</a>            <a href="?page=setting_themes_technoavar_tab_three" class="nav-tab <?php echo $tab === 'tab_three' ? 'nav-tab-active' : ''; ?>">منتخب سردبیر</a>            <a href="?page=setting_themes_technoavar_tab_four" class="nav-tab <?php echo $tab === 'tab_four' ? 'nav-tab-active' : ''; ?>">شبکه‌های اجتماعی</a>            <a href="?page=setting_themes_technoavar_tab_five" class="nav-tab <?php echo $tab === 'tab_five' ? 'nav-tab-active' : ''; ?>">تبلیغات</a>        </h1>        <div class="tab-content">			<?php			switch ( $tab ) {				case 'tab_one':				general_setting_html_layout();					break;				case 'tab_two':					slider_setting_html_layout();					break;				case 'tab_three':					editor_list_setting_html_layout();					break;				case 'tab_four':					social_media_html_layout();					break;				case 'tab_five':					advertising_management_html_layout();					break;				default:					info_setup_theme();			}			?>        </div>    </div>	<?php}function slider_setting_html_layout() {	require_once 'view/admin/slider_setting_html_layout.php';}function social_media_html_layout() {	require_once 'view/admin/social_media_html_layout.php';}function general_setting_html_layout() {	require_once 'view/admin/general_setting_html_layout.php';}function advertising_management_html_layout() {	require_once 'view/admin/advertising_management_html_layout.php';}function editor_list_setting_html_layout() {	require_once 'view/admin/editor_list_setting_html_layout.php';}function info_setup_theme() {	require_once 'view/admin/info_theme_setup.php';}