<?php include_once 'delete_editor_list.php'; ?><div class="uk-container">	<div class="uk-flex-inline uk-flex-stretch uk-margin-top">		<h4 class="uk-margin-left uk-text-right"><?php echo esc_html(get_admin_page_title()); ?></h4>	</div>	<div class="uk-overflow-auto">		<table class="uk-table uk-table-hover uk-table-middle uk-table-divider">			<thead>			<tr>				<th class="uk-table-shrink uk-text-right">#</th>				<th class="uk-table-expand uk-text-right">تصویر</th>				<th class="uk-table-expand uk-text-right">عنوان مقاله</th>				<th class="uk-width-small uk-text-right">نویسنده</th>				<th class="uk-table-shrink uk-text-right">دسته‌بندی</th>				<th class="uk-table-shrink uk-text-right">تاریخ</th>				<th class="uk-table-shrink uk-text-right">عملیات</th>			</tr>			</thead>			<tbody>			<?php			$args = [				'post_type' => 'post',				'meta_key' => '_check_editor',				'meta_value' => 'yes'			];			$query = new WP_Query($args);			$counter = 1;			if ($query->have_posts()) :				while ($query->have_posts()) : $query->the_post();					?>					<tr>						<td class="uk-text-right"><?php echo $counter++; ?></td>						<td class="uk-text-right">							<div class="uk-flex uk-flex-middle uk-flex-right">								<?php if (has_post_thumbnail()) : ?>									<img src="<?php echo get_the_post_thumbnail_url(); ?>" alt="تصویر" class="uk-border-rounded" style="width: 100px; height: auto; object-fit: cover;">								<?php else : ?>									<span class="uk-text-muted">بدون تصویر</span>								<?php endif; ?>							</div>						</td>						<td class="uk-table-link uk-text-right">							<a class="uk-link-reset" href="<?php the_permalink(); ?>">								<?php the_title(); ?>							</a>						</td>						<td class="uk-text-right">							<a class="uk-link-reset" href="<?php echo esc_url(get_author_posts_url(get_the_author_meta('ID'))); ?>">								<?php the_author(); ?>							</a>						</td>						<td class="uk-table-shrink uk-text-right">							<?php							$cat_id = get_post_meta(get_the_ID(), '_tnw_category_post', true);							if ($cat_id) {								$cat_name = get_the_category_by_ID($cat_id);								?>								<span class="uk-label uk-label-success"><?php echo esc_html($cat_name); ?></span>							<?php } else { ?>								<span class="uk-label uk-label-warning">بدون دسته</span>							<?php } ?>						</td>						<td class="uk-table-shrink uk-text-nowrap uk-text-right">							<span class="uk-text-small uk-text-muted"><?php echo get_the_date('j F, Y'); ?></span>						</td>						<td>							<div class="uk-button-group">								<a href="<?php echo esc_url(add_query_arg(['action' => 'delete', 'id' => get_the_ID()])); ?>" uk-tooltip="حذف پست از لیست سر دبیر" class="uk-icon-link uk-margin-small-right"><span uk-icon="trash"></span></a>								<a href="<?php echo get_edit_post_link(); ?>" uk-tooltip="ویرایش خبر" class="uk-icon-link"><span uk-icon="file-edit"></span></a>							</div>						</td>					</tr>				<?php endwhile; else : ?>				<tr>					<td colspan="7" class="uk-text-right">						<div class="uk-alert-primary uk-padding-small" uk-alert>							<p>هیچ پستی  توسط سر دبیر انتخاب نشده است!</p>						</div>					</td>				</tr>			<?php endif; ?>			</tbody>		</table>	</div></div>