<?phpadd_action('wp_dashboard_setup','tnw_register_dashboard_widget');function tnw_register_dashboard_widget() {	wp_add_dashboard_widget(		'tnw_widget'		,'آمار و نوع پست ها'		,'tnw_widget_html'		,null		,null		,'side',		'low'	);}function tnw_widget_html() {	// تعداد پست‌های تصویری (با فرض اینکه کلید meta_key مربوطه وجود دارد)	$image_posts = new WP_Query(array(		'meta_key' => '_type_post_meta',		'meta_value' => 'image', // مقدار مربوط به پست تصویری		'posts_per_page' => -1,		'fields' => 'ids'	));	$image_count = $image_posts->post_count;	// تعداد پست‌های ویدیویی	$video_posts = new WP_Query(array(		'meta_key' => '_type_post_meta',		'meta_value' => 'video', // مقدار مربوط به پست ویدیویی		'posts_per_page' => -1,		'fields' => 'ids'	));	$video_count = $video_posts->post_count;	// تعداد پست‌های متنی	$text_posts = new WP_Query(array(		'meta_key' => '_type_post_meta',		'meta_value' => 'text', // مقدار مربوط به پست متنی		'posts_per_page' => -1,		'fields' => 'ids'	));	$text_count = $text_posts->post_count;	// تعداد پست‌های اسلایدر (با فرض کلید meta_key متفاوت)	$slider_posts = new WP_Query(array(		'meta_key' => '_check_slider',		'meta_value' => 'yes', // مقدار مربوط به پست اسلایدر		'posts_per_page' => -1,		'fields' => 'ids'	));	$slider_count = $slider_posts->post_count;	// تعداد پست‌های منتخب سردبیر (با فرض کلید meta_key متفاوت)	$editor_posts = new WP_Query(array(		'meta_key' => '_check_editor',		'meta_value' => 'yes', // مقدار مربوط به پست منتخب سردبیر		'posts_per_page' => -1,		'fields' => 'ids'	));	$editor_count = $editor_posts->post_count;	?>    <div class="uk-container uk-container-xsmall uk-background-muted uk-padding uk-border-rounded uk-box-shadow-medium">        <div class="uk-grid-small uk-child-width-expand@s uk-margin-small" uk-grid>            <div>                <span class="uk-text-bold"><span class="uk-margin-left" uk-icon="image"></span>پست تصویری:</span>            </div>            <div class="uk-text-right">                <span class="uk-text-primary"><?php echo $image_count; ?></span>            </div>        </div>        <hr>        <div class="uk-grid-small uk-child-width-expand@s uk-margin-small" uk-grid>            <div>                <span class="uk-text-bold"><span class="uk-margin-left" uk-icon="video-camera"></span>پست ویدیویی:</span>            </div>            <div class="uk-text-right">                <span class="uk-text-secondary"><?php echo $video_count; ?></span>            </div>        </div>        <hr>        <div class="uk-grid-small uk-child-width-expand@s uk-margin-small" uk-grid>            <div>                <span class="uk-text-bold"><span class="uk-margin-left" uk-icon="file-text"></span>پست متن:</span>            </div>            <div class="uk-text-right">                <span class="uk-text-danger"><?php echo $text_count; ?></span>            </div>        </div>        <hr>        <div class="uk-grid-small uk-child-width-expand@s uk-margin-small" uk-grid>            <div>                <span class="uk-text-bold"><span class="uk-margin-left" uk-icon="heart"></span>تعداد پست در اسلایدر</span>            </div>            <div class="uk-text-right">                <span class="uk-text-secondary"><?php echo $slider_count; ?></span>            </div>        </div>        <hr>        <div class="uk-grid-small uk-child-width-expand@s uk-margin-small" uk-grid>            <div>                <span class="uk-text-bold"><span class="uk-margin-left" uk-icon="crosshairs"></span>تعداد پست منتخب سردبیر</span>            </div>            <div class="uk-text-right">                <span class="crosshairs"><?php echo $editor_count; ?></span>            </div>        </div>    </div>	<?php}