<?phpadd_shortcode( 'tnw-short-photo', 'tnw_short_code_image' );add_shortcode( 'tnw-short-video', 'tnw_short_code_video' );add_shortcode( 'tnw-short-qb', 'tnw_short_code_bq' );function tnw_short_code_image( $attr, $content = null ): string {	$src  = isset( $attr['src'] ) ? esc_url( $attr['src'] ) : '';	$href = isset( $attr['href'] ) ? esc_url( $attr['href'] ) : '#';	if ( empty( $src ) ) {		return '<p style="color: red;">تصویر مشخص نشده است.</p>';	}	$output = '<div class="tnw-image-container" style="overflow: hidden;">';	$output .= '<figure class="figure mb-1 m-0">'; // m-0 برای حذف marginهای پیشفرض	$output .= '<a href="' . $href . '" data-glightbox data-gallery="image-popup">';	$output .= '<img class="rounded w-100 h-auto" src="' . $src . '" style="display: block; max-width: 100%; height: auto;">';	$output .= '</a>';	$output .= '</figure>';	$output .= '</div>';	return $output;}function tnw_short_code_video( $attr, $content = null ): string {	$src  = isset( $attr['src'] ) ? esc_url( $attr['src'] ) : '';	$href = isset( $attr['href'] ) ? esc_url( $attr['href'] ) : '#';	if ( empty( $src ) || empty( $href ) ) {		return '<p style="color: red;">ویدیو یا تصویر مشخص نشده است.</p>';	}	return '                <figure class="figure position-relative" id="video-manger">    <img class="rounded w-100" src="' . $src . '" alt="Image">    <div class="position-absolute top-50 start-50 translate-middle">        <a href="' . $href . '" class="icon-lg bg-danger text-white rounded-circle d-flex align-items-center justify-content-center" data-glightbox="" data-gallery="y-video" style="width: 60px; height: 60px;">            <i class="bi bi-play-btn fs-4"></i>        </a>    </div></figure>        ';}function tnw_short_code_bq( $attr, $content = null ): string {	$text  = isset( $attr['text'] ) ? esc_html( $attr['text'] ) : 'متن نقل‌قول مشخص نشده است.';	$quote = isset( $attr['quote'] ) ? esc_html( $attr['quote'] ) : 'ناشناس';	return '        <figure class="bg-light p-3 p-md-5 my-5">            <blockquote class="blockquote">                <p>' . $text . '</p>            </blockquote>            <figcaption class="blockquote-footer">                ' . $quote . '            </figcaption>        </figure>    ';}