<?phpadd_filter( 'mce_external_plugins', 'tnw_add_tinymce_plugin_js' );add_filter( 'mce_buttons', 'tnw_register_custom_button_for_tinymce' );function tnw_add_tinymce_plugin_js( $plugins ) {	$plugins['custom_buttons'] = TNW_URL . '/assets/js/tinymce-buttons.js';	return $plugins;}function tnw_register_custom_button_for_tinymce( $buttons ) {	$buttons[] = 'video';	$buttons[] = 'quote';	$buttons[] = 'photo';	return $buttons;}