<?phpfunction tnw_register_sidebar() {	$args1 = [		'name'          => 'tnw_theme_cat_post_sidebar',		'id'            => 'sidebar-1',		'description'   => 'ساید بار قالب',		'before_widget' => '<div id="%1$s" class="widget %2$s">',		'after_widget'  => '</div>',		'before_title'  => '<h6 class="widgettitle">',		'after_title'   => '</h6>',		'class' =>'d-flex justify-content-between align-items-center bg-warning bg-opacity-15 rounded p-2 position-relative'	];	register_sidebar($args1);}add_action('widgets_init','tnw_register_sidebar');