<?phpadd_action('after_setup_theme','theme_setup');function theme_setup() {	require_once TNW_DIR.'/class/nav_walker/bootstrap_5_wp_nav_menu_walker.php';	register_nav_menu('main-menu', 'Main menu');	register_nav_menu('footer-menu', 'footer menu');	//ADD THUMB YO THEME	add_theme_support('woocommerce');	add_theme_support('post-thumbnails');	//ADD UPLOAD IMAGE	add_image_size('my_size', '300','300',array('center','center') ,);	//REMOVE NAV BAE	add_filter('show_admin_bar','__return_false');}