<?phpclass Breadcrumb {	public static function get_breadcrumb() {		echo '<li class="breadcrumb-item"><a href="' . home_url() . '"rel="nofollow"><i class="bi bi-house me-1"></i> خانه</a></li>';		if ( is_single() || is_search() || is_tag() ) {			if ( is_single() ) {				$category = get_the_category();				if ( ! empty( $category ) ) {					$cat_link = get_category_link( $category[0]->term_id );					echo '<li class="breadcrumb-item"><a href="' . esc_url( $cat_link ) . '">' . esc_html( $category[0]->name ) . '</a></li>';				}				echo '<li class="breadcrumb-item " aria-current="page">' . get_the_title() . '</li>';			}		}		if ( is_page() ) {			the_title( '<li class="breadcrumb-item text-body">', '</li>', true );		}		if ( is_search() ) {			echo '<li class="breadcrumb-item"></li>';			echo '<em>';			echo get_search_query(); // به جای the_search_query از get_search_query استفاده کنید			echo '</em>';		}		if ( is_category() ) {			$category = get_queried_object(); // دریافت دسته‌بندی فعلی			if ( $category ) {				$category_link = get_category_link( $category->term_id );				$category_name = $category->name; // نام دسته‌بندی				if ( $category_link && $category_name ) {					echo '<li class="breadcrumb-item"><a href="' . esc_url( $category_link ) . '">' . esc_html( $category_name ) . '</a></li>';				}			}		}		if ( is_tag() ) {			echo '<li class="breadcrumb-item"></li>';			single_tag_title();		}		if ( is_archive() ) {			if ( is_year() ) {				echo '<li class="breadcrumb-item">' . get_the_time( 'آرشیو سال Y' ) . '</li>';			} elseif ( is_month() ) {				echo '<li class="breadcrumb-item">' . get_the_time( 'آرشیو ماه M' ) . '</li>';			} elseif ( is_day() ) {				echo '<li class="breadcrumb-item">' . get_the_time() . '</li>';			}			if ( is_page( 'contact' ) ) {				echo '<li class="breadcrumb-item">تماس با ما </li>';			}		}	}}