<!-- Comments START --><div class="mt-5">	<?php $comment_num = get_comments_number();	     if ($comment_num > 0 ) :	?>	<h3><?php echo get_comments_number() ?>  دیدگاه</h3>	     <?php else: ?>	     <h3>شما اولین کسی باشد که کامنت بگذارید!!!</h3>	     <?php endif; ?>	<!-- Comment level 1-->	<ul class="my-4">		<?php              $args = [				  'avatar_size'=>'45',	              'style'=>'ul',	              'callback'=>'tnw_comment_func',	              'max_depth'=>3,              ];		wp_list_comments($args); ?>	</ul>    <!-- Reply START -->	<?php if (comments_open()) : ?>        <!-- Comment Modal -->        <div class="modal fade" id="comment-modal" tabindex="-1" aria-labelledby="commentModalLabel" aria-hidden="true">            <div class="modal-dialog modal-dialog-centered">                <div class="modal-content">                    <div class="modal-header">                        <h5 class="modal-title reply-to"></h5>                        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>                    </div>                    <div class="modal-body">                        <div class="alert alert-success main-comment-content"></div>                        <form action="<?php echo site_url() . '/wp-comments-post.php'; ?>" method="post">                            <div class="row">								<?php if ( ! is_user_logged_in() ): ?>                                    <div class="col-lg-6">                                        <div class="form-group">                                            <input type="text" class="form-control" name="author" placeholder="نام کامل" required>                                        </div>                                    </div>                                    <div class="col-lg-6">                                        <div class="form-group">                                            <input type="email" class="form-control" name="email" placeholder="ایمیل معتبر" required>                                        </div>                                    </div>                                    <div class="col-lg-12">                                        <p class="comment-form-cookies-consent">                                            <input id="wp-comment-cookies-consent" name="wp-comment-cookies-consent" type="checkbox" value="yes">                                            <label for="wp-comment-cookies-consent">ذخیره نام، ایمیل و وبسایت من در مرورگر برای زمانی که دوباره دیدگاهی می‌نویسم.</label>                                        </p>                                    </div>								<?php endif; ?>                                <div class="col-lg-12">                                    <div class="form-group">                                        <textarea name="comment" class="form-control" cols="30" rows="6" placeholder="نظر خود را بنویسید..." required></textarea>                                    </div>                                </div>                            </div>                            <div class="modal-footer">                                <button type="submit" class="btn btn-primary">ثبت</button>                                <input type="hidden" name="comment_post_ID" value="<?php echo get_the_ID(); ?>" id="comment_post_ID">                                <input type="hidden" name="comment_parent" id="comment_parent" value="0">								<?php if (is_user_logged_in()): ?>                                    <input type="hidden" id="_wp_unfiltered_html_comment_disabled" name="_wp_unfiltered_html_comment" value="<?php echo wp_create_nonce(''); ?>">								<?php endif; ?>                            </div>                        </form>                    </div>                </div>            </div>        </div>        <!-- Comment Form Outside Modal -->		<?php if (get_option('comment_registration') && !is_user_logged_in()) : ?>            <div class="alert alert-info">کاربر گرامی قبل از ثبت دیدگاه باید ثبت نام کنید!</div>		<?php else: ?>            <h3>ثبت دیدگاه</h3>			<?php if (is_user_logged_in()): ?>				<?php $user = wp_get_current_user(); ?>                <div class="alert alert-info">					<?php echo esc_html($user->display_name); ?> عزیز، خوش آمدید. می‌توانید دیدگاه خود را بنویسید.                </div>			<?php elseif (get_option('comment_registration')): ?>                <div class="alert alert-warning">                    فقط کاربران وارد شده می‌توانند دیدگاه ثبت کنند. لطفاً ابتدا وارد شوید یا ثبت‌نام کنید.                </div>			<?php else: ?>                <small class="form-text text-muted mb-3">                    ایمیل شما منتشر نخواهد شد. فیلدهای الزامی با * مشخص شده‌اند.                </small>			<?php endif; ?>            <form class="row g-3 mt-2" method="post" action="<?php echo site_url('/wp-comments-post.php'); ?>">				<?php if (!is_user_logged_in()): ?>                    <div class="col-md-6">                        <label class="form-label">نام *</label>                        <input type="text" class="form-control" name="author" placeholder="نام..." required>                    </div>                    <div class="col-md-6">                        <label class="form-label">ایمیل *</label>                        <input type="email" class="form-control" name="email" placeholder="ایمیل..." required>                    </div>                    <!-- custom checkbox -->                    <div class="col-md-12">                        <div class="form-check">                            <input class="form-check-input" type="checkbox" name="wp-comment-cookies-consent" value="yes" id="flexCheckDefault">                            <label class="form-check-label" for="flexCheckDefault">برای دفعه بعد که نظر دادم، نام و ایمیل من در این مرورگر ذخیره شود.</label>                        </div>                    </div>				<?php endif; ?>                <div class="col-12">                    <label class="form-label">متن دیدگاه *</label>                    <textarea class="form-control" name="comment" rows="3" placeholder="دیدگاه خود را بنویسید..." required></textarea>                </div>                <div class="col-12">                    <input type="submit" class="btn btn-primary" name="submit" id="submit" value="ثبت">                    <input type="hidden" name="comment_post_ID" value="<?php echo get_the_ID(); ?>" id="comment_post_ID">                    <input type="hidden" name="comment_parent" id="comment_parent" value="0">					<?php if (is_user_logged_in()): ?>                        <input type="hidden" id="_wp_unfiltered_html_comment_disabled" name="_wp_unfiltered_html_comment" value="<?php echo wp_create_nonce(''); ?>">					<?php endif; ?>                </div>            </form>		<?php endif; ?>		<?php if (!comments_open()): ?>            <div class="alert alert-info">کامنت‌های این پست بسته شده است!</div>		<?php endif; ?>	<?php endif; ?>    <!-- Reply END --></div>