<!DOCTYPE html><html lang="<?php  language_attributes(); ?>" dir="rtl"><head>    <title><?php bloginfo('name'); ?> | <?php is_front_page() ? bloginfo('description') : wp_title(''); ?></title>    <!-- Meta Tags -->	<meta charset="utf-8">	<meta name="viewport" content="width=device-width, initial-scale=1, shrink-to-fit=no">	<meta name="author" content="Blogzine">	<meta name="description" content="قالب وبلاگ و مجله خبری مبتنی بر بوت استرپ">	<!-- Dark mode -->	<script>        const storedTheme = localStorage.getItem('theme')        const getPreferredTheme = () => {            if (storedTheme) {                return storedTheme            }            return window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light'        }        const setTheme = function (theme) {            if (theme === 'auto' && window.matchMedia('(prefers-color-scheme: dark)').matches) {                document.documentElement.setAttribute('data-bs-theme', 'dark')            } else {                document.documentElement.setAttribute('data-bs-theme', theme)            }        }        setTheme(getPreferredTheme())        window.addEventListener('DOMContentLoaded', () => {            var el = document.querySelector('.theme-icon-active');            if(el != 'undefined' && el != null) {                const showActiveTheme = theme => {                    const activeThemeIcon = document.querySelector('.theme-icon-active use')                    const btnToActive = document.querySelector(`[data-bs-theme-value="${theme}"]`)                    const svgOfActiveBtn = btnToActive.querySelector('.mode-switch use').getAttribute('href')                    document.querySelectorAll('[data-bs-theme-value]').forEach(element => {                        element.classList.remove('active')                    })                    btnToActive.classList.add('active')                    activeThemeIcon.setAttribute('href', svgOfActiveBtn)                }                window.matchMedia('(prefers-color-scheme: dark)').addEventListener('change', () => {                    if (storedTheme !== 'light' || storedTheme !== 'dark') {                        setTheme(getPreferredTheme())                    }                })                showActiveTheme(getPreferredTheme())                document.querySelectorAll('[data-bs-theme-value]')                    .forEach(toggle => {                        toggle.addEventListener('click', () => {                            const theme = toggle.getAttribute('data-bs-theme-value')                            localStorage.setItem('theme', theme)                            setTheme(theme)                            showActiveTheme(theme)                        })                    })            }        })	</script>	<!-- Favicon -->	<link rel="shortcut icon" href="<?php echo TNW_URL .'assets/images/favicon.ico'?>">  <?php wp_head(); ?></head><body <?php  body_class(); ?>><!-- Preloader START--><!--<div class="preloader">--><!--	<div class="loader">--><!--		<div class="sh1"></div>--><!--		<div class="sh2"></div>--><!--	</div>--><!--</div>--><!-- Preloader END--><!-- =======================Header START -->