<?php$paged = (get_query_var('paged')) ? get_query_var('paged') : 1;$args = ['post_type'      => 'post','posts_per_page' => 8,'paged'          => $paged,];$the_query = new WP_Query($args);?><?php if ($the_query->have_posts()) :	while ($the_query->have_posts()) : $the_query->the_post(); ?>		<div class="col-sm-6 col-lg-3">			<div class="card mb-4">				<!-- Card img -->				<div class="card-fold position-relative">					<?php echo tnw_post_thumbnail(); ?>					<div class="card-img-overlay d-flex align-items-start flex-column p-3">						<!-- Card overlay Top -->						<div class="w-100 mb-auto d-flex justify-content-end">							<div class="text-end ms-auto">								<!-- Card format icon -->								<?php								      $post_type = get_post_meta(get_the_ID(),'_type_post_meta',true);									  if ($post_type){										  switch ($post_type){											  case 'text';											  echo '<div class="icon-md bg-blur text-white fw-bold rounded-circle" title="This post has video"><i class="fas fa-file"></i></div>';											  break;											  case 'video';												  echo '<div class="icon-md bg-blur text-white fw-bold rounded-circle" title="This post has video"><i class="fas fa-video"></i></div>';												  break;											  case 'img';												  echo '<div class="icon-md bg-blur text-white fw-bold rounded-circle" title="This post has video"><i class="fas fa-images"></i></div>';												  break;										  }									  }else{										 echo   '<div class="icon-md bg-danger text-white fw-bold rounded-circle" title="This post has video"><i class="fas fa-info"></i></div>';									  }								?>							</div>						</div>					</div>				</div>				<div class="card-body px-0 pt-3">					<h5 class="card-title"><a href="<?php the_permalink(); ?>" class="btn-link text-reset stretched-link"><?php the_title() ?></a></h5>					<!-- Card info -->					<ul class="nav nav-divider align-items-center text-uppercase small">						<li class="nav-item">							<a href="#" class="nav-link text-reset btn-link"><?php the_author() ?></a>						</li>						<li class="nav-item"><?php echo get_the_date('j F  , Y') ?></li>					</ul>				</div>			</div>		</div><?php endwhile; ?><?php else : ?><div class="alert alert-info">پستی وجود ندارد!</div><?php endif; ?><div class="col-12 text-center mt-5">    <nav class="mb-5 d-flex justify-content-center" aria-label="navigation">		<?php echo Pagination::paginate($the_query,'list'); ?>    </nav></div>