<?php if (have_posts()):    while (have_posts()) : the_post() ; ?><div class="col-sm-6 col-lg-4">	<div class="card">		<!-- Card img -->		<div class="position-relative">            <?php echo tnw_post_thumbnail(); ?>			<div class="card-img-overlay d-flex align-items-start flex-column p-3">				<!-- Card overlay bottom -->				<div class="w-100 mt-auto">					<!-- Card category -->					<?php if (empty(get_post_meta(get_the_ID(),'_tnw_category_post',true))):?>                        <a href="#" class="badge text-bg-danger mb-2"><i class="fas fa-circle me-2 small fw-bold"></i>لطفا دسته بندی مورد نظر از طریق متا باکس موجود انتخاب کنید!!</a>					<?php else: ?>						<?php $cat_id = get_post_meta(get_the_ID(),'_tnw_category_post',true) ;						$cat_name = get_the_category_by_ID($cat_id);						$cat_link = get_category_link($cat_id); ?>                        <a href="<?php echo $cat_link ?>" class="badge text-bg-warning mb-2"><i class="fas fa-circle me-2 small fw-bold"></i><?php echo $cat_name ?></a>					<?php endif; ?>				</div>			</div>		</div>		<div class="card-body px-0 pt-3">			<h4 class="card-title"><a href="<?php the_permalink(); ?>" class="btn-link text-reset"><?php the_title(); ?></a></h4>			<p class="card-text"><?php echo PostExcerpt::tnw_post_excerpt(); ?></p>			<!-- Card info -->			<ul class="nav nav-divider align-items-center d-none d-sm-inline-block">				<li class="nav-item">					<div class="nav-link">						<div class="d-flex align-items-center position-relative">							<div class="avatar avatar-xs">                                <?php                                $id_or_email =  get_the_author_meta('email');                                $avatar =  get_avatar($id_or_email,'39','robohash',get_the_author(),['class'=>'avatar-img rounded-circle']);                                echo $avatar ;                                ?>							</div>							<span class="ms-3"><?php echo get_the_author(); ?></span>						</div>					</div>				</li>				<li class="nav-item"><?php echo get_the_date('j F , Y') ?></li>                <li class="nav-item"><span class="fa fa-comment"></span>  <?php echo  get_comments_number(); ?>  </li>            </ul>		</div>	</div></div><?php endwhile; ?>    <div class="col-12 mt-5">        <nav class="d-flex justify-content-center" aria-label="Page navigation">            <ul class="pagination pagination-bordered">				<?php				global $wp_query;				$big = 999999999;				$current = max(1, get_query_var('paged'));				$total = $wp_query->max_num_pages;				// Previous link				if ($current > 1) {					echo '<li class="page-item"><a class="page-link" href="' . esc_url(get_pagenum_link($current - 1)) . '"><i class="fas fa-chevron-right me-1"></i> قبل</a></li>';				} else {					echo '<li class="page-item disabled"><a class="page-link" href="#" tabindex="-1" aria-disabled="true"><i class="fas fa-chevron-right me-1"></i> قبل</a></li>';				}				// First page				if ($current > 2) {					echo '<li class="page-item"><a class="page-link" href="' . esc_url(get_pagenum_link(1)) . '">1</a></li>';					if ($current > 3) {						echo '<li class="page-item disabled"><span class="page-link">...</span></li>';					}				}				// Middle pages				$start = max(1, $current - 1);				$end = min($total, $current + 1);				for ($i = $start; $i <= $end; $i++) {					if ($i == $current) {						echo '<li class="page-item active"><span class="page-link">' . $i . '</span></li>';					} else {						echo '<li class="page-item"><a class="page-link" href="' . esc_url(get_pagenum_link($i)) . '">' . $i . '</a></li>';					}				}				// Last page				if ($current < $total - 1) {					if ($current < $total - 2) {						echo '<li class="page-item disabled"><span class="page-link">...</span></li>';					}					echo '<li class="page-item"><a class="page-link" href="' . esc_url(get_pagenum_link($total)) . '">' . $total . '</a></li>';				}				// Next link				if ($current < $total) {					echo '<li class="page-item"><a class="page-link" href="' . esc_url(get_pagenum_link($current + 1)) . '">بعد <i class="fas fa-chevron-left ms-1"></i></a></li>';				} else {					echo '<li class="page-item disabled"><a class="page-link" href="#" tabindex="-1" aria-disabled="true">بعد <i class="fas fa-chevron-left ms-1"></i></a></li>';				}				?>            </ul>        </nav>    </div><?php else: ?><div class="alert alert-info">این نویسنده پستی منشتر نکرده است!!!</div><?php endif ?>