<?php$paged = ( get_query_var('paged') ) ? get_query_var('paged') : 1;$category_id = get_queried_object_id();$args = [	'post_type' => 'post',	'posts_per_page' => 4,	'cat' => $category_id,	'paged' => $paged,];$the_query = new WP_Query( $args );?><!-- Card img --><?php if ( $the_query->have_posts() ) : ?>	<?php while ( $the_query->have_posts() ) : $the_query->the_post(); ?><div class="col-sm-6">	<div class="card">		<!-- Card img -->		<div class="position-relative">			<?php echo tnw_post_thumbnail(); ?>			<div class="card-img-overlay d-flex align-items-start flex-column p-3">				<!-- Card overlay bottom -->				<div class="w-100 mt-auto">					<!-- Card category -->					<?php if (empty(get_post_meta(get_the_ID(),'_tnw_category_post',true))):?>						<a href="#" class="badge text-bg-danger mb-2"><i class="fas fa-circle me-2 small fw-bold"></i>لطفا دسته بندی مورد نظر از طریق متا باکس موجود انتخاب کنید!!</a>					<?php else: ?>						<?php $cat_id = get_post_meta(get_the_ID(),'_tnw_category_post',true) ;						$cat_name = get_the_category_by_ID($cat_id);						$cat_link = get_category_link($cat_id); ?>						<a href="<?php echo $cat_link ?>" class="badge text-bg-warning mb-2"><i class="fas fa-circle me-2 small fw-bold"></i><?php echo $cat_name ?></a>					<?php endif; ?>				</div>			</div>		</div>		<div class="card-body px-0 pt-3">			<h4 class="card-title"><a href="<?php the_permalink(); ?>" class="btn-link text-reset"><?php the_title(); ?></a></h4>			<p class="card-text"><?php echo PostExcerpt::tnw_post_excerpt(); ?></p>			<!-- Card info -->			<ul class="nav nav-divider align-items-center d-none d-sm-inline-block">				<li class="nav-item">					<div class="nav-link">						<div class="d-flex align-items-center position-relative">							<span class="ms-3"><i class="fa fa-user-edit me-2 small fw-bold"></i><?php echo get_the_author(); ?></span>						</div>					</div>				</li>                <li class="nav-item"><i class="fa fa-calendar me-2 small fw-bold"></i><?php echo get_the_date('j F, Y') ; ?></li>                <li class="nav-item"><i class="fas fa-eye me-2 small fw-bold"></i>  <?php echo  PostView::tnw_get_post_view(get_the_ID()) ; ?> بازدید   </li>            </ul>		</div>	</div></div><?php endwhile; ?><?php else :?><div class="alert alert-info">پستی منتشر نشده است !</div><?php endif; ?><div class="col-12 text-center mt-5">    <nav class="mb-5 d-flex justify-content-center" aria-label="navigation">		<?php echo Pagination::paginate($the_query,'list'); ?>    </nav></div>