<?php$args = [	'post_type' => 'post',	'posts_per_page' => 9,	'orderby' => 'date',	'order' => 'DESC'];$the_query = new WP_Query($args);if ($the_query->have_posts()):	While($the_query->have_posts()) : $the_query->the_post()?><div class="col-sm-6 col-lg-4">	<div class="card card-overlay-bottom card-image-scale">		<!-- Card Image -->		<?php echo tnw_post_thumbnail(); ?>		<!-- Card Image overlay -->		<div class="card-img-overlay d-flex flex-column p-3 p-md-4">			<div>				<!-- Card category -->				<?php if (empty(get_post_meta(get_the_ID(),'_tnw_category_post',true))):?>					<a href="#" class="badge text-bg-danger mb-2"><i class="fas fa-circle me-2 small fw-bold"></i>لطفا دسته بندی مورد نظر از طریق متا باکس موجود انتخاب کنید!!</a>				<?php else: ?>					<?php                    $cat_id = get_post_meta(get_the_ID(),'_tnw_category_post',true) ;					$cat_name = get_the_category_by_ID($cat_id);					$cat_link = get_category_link($cat_id); ?>					<a href="<?php echo $cat_link ?>" class="badge text-bg-warning mb-2"><i class="fas fa-circle me-2 small fw-bold"></i><?php echo $cat_name ?></a>				<?php endif; ?>			</div>			<div class="w-100 mt-auto">				<!-- Card title -->				<h4 class="text-white"><a href="<?php the_permalink() ?>" class="btn-link text-reset stretched-link"><?php the_title(); ?></a></h4>				<!-- Card info -->				<ul class="nav nav-divider text-white-force align-items-center small">					<li class="nav-item position-relative">						<div class="nav-link stretched-link text-reset"><?php the_author() ?></div>					</li>					<li class="nav-item"><?php echo get_the_date('j F, Y'); ?></li>                    <li class="nav-item"><i class="fa fa-user-clock me-2 small fw-bold"></i><?php echo ReadPost::tnw_read_post_time(get_the_content()); ?> ` </li>                </ul>			</div>		</div>	</div></div><?php endwhile; ?><?php else: ?><div class="alert alert-info">پستی منتشر نشده است!</div><?php endif; ?>