<?php$args = [	'post_type' => 'post',	'posts_per_page' => 6,	'meta_key' => '_check_editor',	'meta_value' => 'yes',	'orderby' => 'date',	'order' => 'DESC'];$the_query = new WP_Query($args);if ($the_query->have_posts()):	$posts_left = [];	$posts_right = [];	$i = 0;	while ($the_query->have_posts()) : $the_query->the_post();		ob_start();		?>		<div class="card mb-3 mb-sm-4">			<div class="row g-3">				<div class="col-4">					<?php echo tnw_post_thumbnail(); ?>				</div>				<div class="col-8">					<?php if (empty(get_post_meta(get_the_ID(), '_tnw_category_post', true))): ?>						<a href="#" class="badge text-bg-danger mb-2">							<i class="fas fa-circle me-2 small fw-bold"></i>							لطفا دسته بندی مورد نظر از طریق متا باکس موجود انتخاب کنید!!						</a>					<?php else:						$cat_id = get_post_meta(get_the_ID(), '_tnw_category_post', true);						$category_class = [							'badge bg-danger bg-opacity-10 text-danger',							'badge bg-info bg-opacity-10 text-info',							'badge bg-success bg-opacity-10 text-success',							'badge bg-warning bg-opacity-15 text-warning',							'badge bg-primary bg-opacity-10 text-primary'						];						$random_class = $category_class[array_rand($category_class)];						$cat_name = get_the_category_by_ID($cat_id);						$cat_link = get_category_link($cat_id);						?>						<a href="<?php echo esc_url($cat_link); ?>" class="<?php echo esc_attr($random_class); ?> mb-2">							<i class="fas fa-circle me-2 small fw-bold"></i><?php echo esc_html($cat_name); ?>						</a>					<?php endif; ?>					<h4>						<a href="<?php the_permalink(); ?>" class="btn-link stretched-link text-reset">							<?php echo get_the_title(); ?>						</a>					</h4>					<!-- Card info -->					<ul class="nav nav-divider align-items-center d-none d-sm-inline-block">						<li class="nav-item">							<div class="nav-link">								<div class="d-flex align-items-center position-relative">									<div class="avatar avatar-xs">										<?php										$email_or_id = get_the_author_meta('email');										echo get_avatar($email_or_id, 39, 'robohash', get_the_author(), ['class' => 'avatar-img rounded-circle']);										?>									</div>									<span class="ms-3">                                        با <a href="<?php $author_id = get_the_author_meta('ID'); echo esc_url(get_author_posts_url($author_id)); ?>" class="stretched-link text-reset btn-link"><?php echo get_the_author(); ?></a>                                    </span>								</div>							</div>						</li>						<li class="nav-item">							<i class="fa fa-calendar me-2 small fw-bold"></i><?php echo get_the_date('j F, Y'); ?>						</li>						<li class="nav-item">							<i class="fas fa-eye me-2 small fw-bold"></i><?php echo PostView::tnw_get_post_view(get_the_ID()); ?> بازدید						</li>					</ul>				</div>			</div>		</div>		<?php		$post_html = ob_get_clean();		if ($i % 2 === 0) {			$posts_left[] = $post_html;		} else {			$posts_right[] = $post_html;		}		$i++;	endwhile;	?>	<div class="row">		<div class="col-lg-6">			<?php foreach ($posts_left as $item) echo $item; ?>		</div>		<div class="col-lg-6">			<?php foreach ($posts_right as $item) echo $item; ?>		</div>	</div><?php else: ?>	<div class="alert alert-info">هنوز هیچ پستی از طرف سر دبیر انتخاب نشده است!</div><?php endif; ?>