<?php$args = [        'post_type'=>'post',        'post_pre_page'=>3,        'meta_key'=>'_check_slider',        'meta_value'=>'yes'];$the_query = new WP_Query($args);if ($the_query->have_posts()):while($the_query->have_posts()) : $the_query->the_post();?><div class="card bg-dark-overlay-3 h-400 h-sm-500 h-md-600 rounded-0" style="background-image:url(<?php echo get_the_post_thumbnail_url()?>); background-position: center left; background-size: cover;">	<!-- Card Image overlay -->	<div class="card-img-overlay d-flex align-items-center p-3 p-sm-5">		<div class="w-100 my-auto">			<div class="col-md-10 col-lg-7 mx-auto text-center">				<!-- Card category -->				<?php if (empty(get_post_meta(get_the_ID(), '_tnw_category_post', true))): ?>                    <a href="#" class="badge text-bg-danger mb-2">                        <i class="fas fa-circle me-2 small fw-bold"></i>                        لطفا دسته بندی مورد نظر از طریق متا باکس موجود انتخاب کنید!!                    </a>				<?php else:					$cat_id = get_post_meta(get_the_ID(), '_tnw_category_post', true);					$category_class = [						'badge bg-danger bg-opacity-10 text-danger',						'badge bg-info bg-opacity-10 text-info',						'badge bg-success bg-opacity-10 text-success',						'badge bg-warning bg-opacity-15 text-warning',						'badge bg-primary bg-opacity-10 text-primary'					];					$random_class = $category_class[array_rand($category_class)];					$cat_name = get_the_category_by_ID($cat_id);					$cat_link = get_category_link($cat_id);					?>                    <a href="<?php echo esc_url($cat_link); ?>" class="<?php echo esc_attr($random_class); ?> mb-2">                        <i class="fas fa-circle me-2 small fw-bold"></i><?php echo esc_html($cat_name); ?>                    </a>				<?php endif; ?>                <!-- Card title -->				<h4 class="text-white display-5"><a href="<?php the_permalink(); ?>" class="btn-link text-reset fw-normal"><?php the_title() ?></a></h4>				<!-- Card info -->				<ul class="nav nav-divider text-white-force align-items-center d-none d-sm-inline-block">					<li class="nav-item">						<div class="nav-link">							<div class="d-flex align-items-center text-white position-relative">								<div class="avatar avatar-sm">                                    <?php                                    $email_or_id = get_the_author_meta('email');                                    echo  get_avatar( $email_or_id,'45','robohash',get_the_author(),['class'=>'avatar-img rounded-circle'])                                    ?>								</div>								<span class="ms-3 stretched-link text-reset"><?php echo get_the_author() ?></span>							</div>						</div>					</li>					<li class="nav-item"><?php echo get_the_date('j F, Y'); ?></li>					<li class="nav-item"><i class="far fa-comment-alt me-1"></i> دیدگاه : <?php echo  get_comments_number(); ?> </li>                    <li class="nav-item"><i class="fa fa-user-clock me-2 small fw-bold"></i><?php echo ReadPost::tnw_read_post_time(get_the_content()); ?> دقیقه زمان مطالعه</li>                </ul>			</div>		</div>	</div></div><?php endwhile; ?><?php else: ?><div class="alert alert-info">هیچ پستی وجود ندارد!!!</div><?php endif; ?>