<?php$args = [	'posts_per_page' => 5,	'meta_key'       => '_tnw_post_view',	'orderby'        => 'meta_value_num',	'order'          => 'DESC'];$query = new WP_Query($args);if ($query->have_posts()) {	$i = 0;	while ($query->have_posts()) {		$query->the_post();		if ($i === 0) {			// پست ویژه (سمت چپ)			?>			<div class="col-lg-7">				<div class="card card-overlay-bottom card-bg-scale h-400 h-lg-560" style="background-image:url(<?php echo get_the_post_thumbnail_url(); ?>); background-position: center left; background-size: cover;">					<div class="card-img-overlay d-flex align-items-center p-3 p-sm-5">						<div class="w-100 mt-auto">							<?php if (empty(get_post_meta(get_the_ID(), '_tnw_category_post', true))): ?>								<a href="#" class="badge text-bg-danger mb-2">									<i class="fas fa-circle me-2 small fw-bold"></i>									لطفا دسته بندی مورد نظر از طریق متا باکس موجود انتخاب کنید!!								</a>							<?php else:								$cat_id = get_post_meta(get_the_ID(), '_tnw_category_post', true);								$category_class = [									'badge bg-danger bg-opacity-10 text-danger',									'badge bg-info bg-opacity-10 text-info',									'badge bg-success bg-opacity-10 text-success',									'badge bg-warning bg-opacity-15 text-warning',									'badge bg-primary bg-opacity-10 text-primary'								];								$random_class = $category_class[array_rand($category_class)];								$cat_name = get_the_category_by_ID($cat_id);								$cat_link = get_category_link($cat_id);								?>								<a href="<?php echo esc_url($cat_link); ?>" class="<?php echo esc_attr($random_class); ?> mb-2">									<i class="fas fa-circle me-2 small fw-bold"></i><?php echo esc_html($cat_name); ?>								</a>							<?php endif; ?>							<h2 class="text-white display-6">								<a href="<?php the_permalink(); ?>" class="btn-link text-reset stretched-link fw-normal"><?php the_title(); ?></a>							</h2>							<ul class="nav nav-divider text-white-force align-items-center d-none d-sm-inline-block">								<li class="nav-item">									<i class="fa fa-calendar me-2 small fw-bold"></i><?php echo get_the_date('j F, Y'); ?>								</li>								<li class="nav-item">									<i class="fas fa-eye me-2 small fw-bold"></i><?php echo PostView::tnw_get_post_view(get_the_ID()); ?> بازدید								</li>								<li class="nav-item">									<i class="fa fa-user me-2 small fw-bold"></i>									<a href="<?php echo esc_url(get_author_posts_url(get_the_author_meta('ID'))); ?>" class="text-white btn-link">										<?php echo get_the_author(); ?>									</a>								</li>							</ul>						</div>					</div>				</div>			</div>			<?php		} else {			// بقیه پست‌ها (سمت راست)			if ($i === 1) echo '<div class="col-lg-5">';			?>			<div class="card mb-3">				<div class="row g-3">					<div class="col-4">						<?php echo tnw_post_thumbnail(); ?>					</div>					<div class="col-8">						<?php if (empty(get_post_meta(get_the_ID(), '_tnw_category_post', true))): ?>							<a href="#" class="badge text-bg-danger mb-2">								<i class="fas fa-circle me-2 small fw-bold"></i>								لطفا دسته بندی مورد نظر از طریق متا باکس موجود انتخاب کنید!!							</a>						<?php else:							$cat_id = get_post_meta(get_the_ID(), '_tnw_category_post', true);							$category_class = [								'badge bg-danger bg-opacity-10 text-danger',								'badge bg-info bg-opacity-10 text-info',								'badge bg-success bg-opacity-10 text-success',								'badge bg-warning bg-opacity-15 text-warning',								'badge bg-primary bg-opacity-10 text-primary'							];							$random_class = $category_class[array_rand($category_class)];							$cat_name = get_the_category_by_ID($cat_id);							$cat_link = get_category_link($cat_id);							?>							<a href="<?php echo esc_url($cat_link); ?>" class="<?php echo esc_attr($random_class); ?> mb-2">								<i class="fas fa-circle me-2 small fw-bold"></i><?php echo esc_html($cat_name); ?>							</a>						<?php endif; ?>						<h6><a href="<?php the_permalink(); ?>" class="btn-link stretched-link text-reset"><?php the_title(); ?></a></h6>						<ul class="nav nav-divider small">							<li class="nav-item">								<i class="fa fa-calendar me-1 small fw-bold"></i><?php echo get_the_date('j F, Y'); ?>							</li>							<li class="nav-item">								<i class="fas fa-eye me-1 small fw-bold"></i><?php echo PostView::tnw_get_post_view(get_the_ID()); ?> بازدید							</li>							<li class="nav-item">								<i class="fa fa-user me-1 small fw-bold"></i>								<a href="<?php echo esc_url(get_author_posts_url(get_the_author_meta('ID'))); ?>" class="text-reset btn-link">									<?php echo get_the_author(); ?>								</a>							</li>						</ul>					</div>				</div>			</div>			<?php			if ($i === 4) echo '</div>';		}		$i++;	}	wp_reset_postdata();} else {	echo '<div class="alert alert-info">پستی برای نمایش وجود ندارد.</div>';}?>