<?php$args=[	'taxonomy' => 'category',	'hide_empty' => false,	'exclude' => get_option('_tnw_settings_set')['exclude_category_id'],	'parent' => 0,	'orderby' => 'id',	'order' => 'ASC'];$categories = get_categories($args);if ($categories):foreach ($categories as $category) :?><div>	<div class="card card-overlay-bottom card-img-scale">       <?php  $cat_image_url = z_taxonomy_image_url($category->term_id) ; ?>        <?php if (!empty($cat_image_url)): ?>		<img class="card-img" src="<?php echo esc_url($cat_image_url ); ?> " alt="card image">        <?php else: ?>            <img class="card-img" src="<?php echo TNW_URL .'/assets/images/blog/1by1/thumb/05.jpg'?>" alt="card image">        <?php endif; ?>		<div class="card-img-overlay d-flex px-3 px-sm-5">			<h5 class="mt-auto mx-auto">				<a href="<?php echo  get_category_link($category->term_id)?>" class="stretched-link btn-link text-white"><?php echo $category->name; ?></a>			</h5>		</div>	</div></div><?php endforeach; ?><?php else: ?><div class="alert alert-info">دسته بندی وجود ندارد!</div><?php endif; ?>