<?php    if (have_posts()):      while (have_posts()) : the_post(); ?><div class="card border rounded-3 up-hover p-4 mb-4 ">	<div class="row g-3">		<div class="col-sm-9">			<!-- Categories -->			<?php if (empty(get_post_meta(get_the_ID(),'_tnw_category_post',true))):?>                <a href="#" class="badge text-bg-danger mb-2"><i class="fas fa-circle me-2 small fw-bold"></i>لطفا دسته بندی مورد نظر از طریق متا باکس موجود انتخاب کنید!!</a>			<?php else: ?>				<?php $cat_id = get_post_meta(get_the_ID(),'_tnw_category_post',true) ;				$cat_name = get_the_category_by_ID($cat_id);				$cat_link = get_category_link($cat_id); ?>                <a href="<?php echo $cat_link ?>" class="badge text-bg-warning mb-2"><i class="fas fa-circle me-2 small fw-bold"></i><?php echo $cat_name ?></a>			<?php endif; ?>			<!-- Title -->			<h4 class="card-title">				<a href="<?php the_permalink(); ?>" class="btn-link text-reset stretched-link"><?php echo get_the_title(); ?></a>			</h4>			<!-- Card info -->			<ul class="nav nav-divider align-items-center d-none d-sm-inline-block">				<li class="nav-item">					<div class="nav-link">						<div class="d-flex align-items-center position-relative">							<div class="avatar avatar-xs">                        <?php                        $id_or_email =  get_the_author_meta('email');                        $avatar =  get_avatar($id_or_email,'39','robohash',get_the_author(),['class'=>'avatar-img rounded-circle']);                                echo $avatar ;							?>							</div>							<span class="ms-3">با <a href="<?php echo esc_url(get_author_posts_url(get_the_author_meta('ID'))); ?>" class="stretched-link text-reset btn-link"><?php echo get_the_author(); ?></a></span>						</div>					</div>				</li>				<li class="nav-item"><?php echo get_the_date('j ,F Y') ?></li>                <li class="nav-item"><span class="fa fa-comment"></span>  <?php echo  get_comments_number(); ?>  </li>			</ul>		</div>		<!-- Image -->		<div class="col-sm-3">            <?php echo tnw_post_thumbnail(); ?>		</div>	</div></div><?php endwhile; ?><?php else : ?><div class="alert alert-info">هیچ پستی یافت نشد!!</div><?php endif ?>