<!-- =======================Inner intro START --><section class="pt-4">	<div class="container">		<div class="row">			<div class="col-12">				<!-- Author info START -->				<div class="bg-primary bg-opacity-10 d-md-flex p-3 p-sm-4 my-3 text-center text-md-start rounded">					<!-- Avatar -->					<div class=" me-0 me-md-4">						<div class="avatar avatar-xxl">                            <?php $email_or_id = get_the_author_meta('email');                                  $avatar = get_avatar($email_or_id ,'128','robohash',get_the_author(),['class'=>'avatar-img rounded-circle']);                                  echo $avatar ;                            ?>						</div>						<!-- Post count -->						<div class="text-center mt-n3 position-relative">							<span class="badge bg-danger fs-6"><?php echo $wp_query->found_posts; ?> خبر</span>						</div>					</div>					<!-- Info -->					<div>						<h2 class="m-0"><?php echo get_the_author(); ?></h2>						<ul class="list-inline">                            <?php $role =  get_the_author_meta('user_level');                                if($role){                                    switch ($role){	                                    case 10 ;		                                    $role_web =  'مدیر';		                                    break;	                                    case 2 ;		                                    $role_web = 'نویسنده';		                                    break;	                                    case 0 ;		                                    $role_web = 'دبال کننده ';		                                    break;	                                    case 1 ;		                                    $role_web = 'مشارکت کننده';		                                    break;	                                    case 7 ;		                                    $role_web = 'ویرایشگر';		                                    break;                                    }                                } ?>							<li class="list-inline-item"><i class="bi bi-person-fill me-1"></i><?php echo $role_web; ?></li>							<li class="list-inline-item"><i class="bi bi-geo-alt me-1"></i><?php get_the_author_meta('city') ?></li>						</ul>                        <?php if (empty(get_the_author_meta('description'))): ?>                        <div class="alert alert-info">لطفا توضیحات نویسنده را کامل کنید!!!</div>                        <?php else: ?>                            <p class="my-2"><?php  echo get_the_author_meta('description'); ?></p>                        <?php endif; ?>						<!-- Social icons -->						<ul class="nav justify-content-center justify-content-md-start">							<li class="nav-item">								<a class="nav-link ps-0 pe-2 fs-5" href="#"><i class="fab fa-facebook-square"></i></a>							</li>							<li class="nav-item">								<a class="nav-link px-2 fs-5" href="#"><i class="fab fa-twitter-square"></i></a>							</li>							<li class="nav-item">								<a class="nav-link px-2 fs-5" href="#"><i class="fab fa-linkedin"></i></a>							</li>						</ul>					</div>				</div>				<!-- Author info END -->			</div>		</div>	</div></section><!-- =======================Inner intro END -->