<!-- Most read --><?php$current_category = get_the_category();$current_category_id = $current_category[0]->term_id;$args = [	'post_type'=>'post',	'posts_per_page'=>'4',	'orderby' => 'meta_value_num',	'meta_key' => '_tnw_post_view',	'order'=>'DESC',	'post__not_in'=>array(get_the_ID()),	'category__in'=>array($current_category_id)];$the_query = new WP_Query($args);?><div class="mt-4">	<h3 class="mb-3">پست های مرتبط </h3>	<div class="tiny-slider dots-creative mt-3 mb-5">		<div class="tiny-slider-inner"		     data-autoplay="false"		     data-hoverpause="true"		     data-gutter="0"		     data-arrow="false"		     data-dots="true"		     data-items="1">			<!-- Card item START -->			<?php if ($the_query->have_posts()): ?>			<?php while($the_query->have_posts()) : $the_query->the_post(); ?>			<div class="card">				<!-- Card img -->				<div class="position-relative">					<?php echo tnw_post_thumbnail(); ?>					<div class="card-img-overlay d-flex align-items-start flex-column p-3">						<!-- Card overlay Top -->						<div class="w-100 mb-auto d-flex justify-content-end">							<div class="text-end ms-auto">								<!-- Card format icon -->								<?php								$post_type = get_post_meta(get_the_ID(),'_type_post_meta',true);								if ($post_type){									switch ($post_type){										case 'text';											echo '<div class="icon-md bg-blur text-white fw-bold rounded-circle" title="This post has video"><i class="fas fa-file"></i></div>';											break;										case 'video';											echo '<div class="icon-md bg-blur text-white fw-bold rounded-circle" title="This post has video"><i class="fas fa-video"></i></div>';											break;										case 'img';											echo '<div class="icon-md bg-blur text-white fw-bold rounded-circle" title="This post has video"><i class="fas fa-images"></i></div>';											break;									}								}else{									echo   '<div class="icon-md bg-danger text-white fw-bold rounded-circle" title="This post has video"><i class="fas fa-info"></i></div>';								}								?>							</div>						</div>						<!-- Card overlay bottom -->						<div class="w-100 mt-auto">							<?php if (empty(get_post_meta(get_the_ID(),'_tnw_category_post',true))):?>								<a href="#" class="badge text-bg-danger mb-2"><i class="fas fa-circle me-2 small fw-bold"></i>لطفا دسته بندی مورد نظر از طریق متا باکس موجود انتخاب کنید!!</a>							<?php else: ?>								<?php $cat_id = get_post_meta(get_the_ID(),'_tnw_category_post',true) ;								$cat_name = get_the_category_by_ID($cat_id);								$cat_link = get_category_link($cat_id); ?>								<a href="<?php echo $cat_link ?>" class="badge text-bg-info mb-2"><i class="fas fa-circle me-2 small fw-bold"></i><?php echo $cat_name ?></a>							<?php endif; ?>						</div>					</div>				</div>				<div class="card-body p-0 pt-3">					<h5 class="card-title"><a href="<?php the_permalink(); ?>" class="btn-link text-reset"><?php the_title(); ?></a></h5>				</div>			</div>			<?php endwhile; ?>			<?php else: ?>			<div class="alert alert-info">هیج پست مشابه ای یافت نشد!!!</div>			<?php endif; ?>			<!-- Card item END -->		</div>	</div></div>					