<div class="row">	<!-- Recent post widget START -->	<div class="col-12 col-sm-6 col-lg-12">		<h4 class="mt-4 mb-3">پربحث ترین ها</h4>		<!-- Recent post item -->		<?php		$args = [			'post_type' => 'post',			'posts_per_page' => 4,			'orderby' => 'comment_count',			'order' => 'DESC'		] ;		$the_query = new WP_Query($args);		if ($the_query->have_posts()) :			while ($the_query->have_posts()) : $the_query->the_post(); ?>		<div class="card mb-3">			<div class="row g-3">				<div class="col-4">				<?php echo tnw_post_thumbnail(); ?>				</div>				<div class="col-8">					<h6><a href="<?php the_permalink(); ?>" class="btn-link stretched-link text-reset"><?php echo get_the_title(); ?></a></h6>					<div class="small mt-1"> <?php echo human_time_diff(get_the_time('U'), current_time('timestamp')) . ' پیش'; ?></div>				</div>			</div>		</div>		<!-- Recent post item -->		<?php endwhile; ?>		<?php endif; ?>	</div>	<!-- Recent post widget END -->