                                  <?php                                                $id_or_email =  get_the_author_meta('email');                                                $avatar =  get_avatar($id_or_email,'39','robohash',get_the_author(),['class'=>'avatar-img rounded-circle']);                                                echo $avatar;                                                ?>											</div>											<span class="ms-3"><i class="fa fa-user-edit me-2 small fw-bold"></i> <a href="<?php $author_id = get_the_author_meta('ID'); echo esc_url(get_author_posts_url($author_id)); ?>" class="stretched-link text-reset btn-link"><?php echo get_the_author(); ?></a></span>										</div>									</div>								</li>                                <?php PostView::tnw_set_post_view(get_the_ID()); ?>                                <?php                                $referer = $_SERVER['PHP_REFERER'] ?? '';                                GoogleReferer::tnw_set_google_referer(get_the_ID(),$referer);                                ?>								<li class="nav-item"><i class="fa fa-calendar me-2 small fw-bold"></i><?php echo get_the_date('j F, Y') ; ?></li>                                <li class="nav-item"><i class="fas fa-eye me-2 small fw-bold"></i>  <?php echo  PostView::tnw_get_post_view(get_the_ID()) ; ?> بازدید   </li>								<li class="nav-item"><i class="fa fa-user-clock me-2 small fw-bold"></i><?php echo ReadPost::tnw_read_post_time(get_the_content()); ?> دقیقه زمان مطالعه</li>							</ul>						</div>						<?php endwhile; ?>						<?php endif; ?>					</div>				</div>			</div>		</div>	</div></section><!-- =======================Inner intro END -->